<?php
 namespace Compassplus\Sdk\Config; use UnexpectedValueException; class Config { const XML = 'XML'; const JSON = 'JSON'; public static $root = 'PROD'; private static $dataFormat; private function __construct() { } public static function getDataFormat() { return self::get('format'); } public static function setDataFormat($dataFormat) { self::$dataFormat = $dataFormat; } public static function get($value) { $array = include 'config_ini.php'; if (array_key_exists($value, $array) && $value != 'testing') { return $array[$value]; } if ((bool)$array['testing']) { self::$root = 'TEST'; } if (empty($array[self::$root][$value])) { throw new UnexpectedValueException('Unknown settings'); } return $array[self::$root][$value]; } public static function getHostName() { return self::get('hostname'); } public static function getPort() { return self::get('port'); } } 