<?php
 namespace Compassplus\Sdk; use Compassplus\Sdk\Operation\OperationType; use Exception; class OrderStatus { private $operation; public function __construct(Order $order, Merchant $merchant, Customer $customer) { $this->operation = new Operation\Operation($order, $customer, $merchant); } public function perform() { $response = $this->send(OperationType::ORDERSTATUS); return new Response\OrderStatus($response); } private function send($operationType) { $data = $this->loadOrderStatus($operationType); $connector = new Connector($data); return $connector->sendRequest(); } private function loadOrderStatus($operationType) { $data = new Request\DataProviderStrategy($operationType, $this->operation); return $data->getPayload(); } } 