<?php
 namespace Compassplus\Sdk\Request\XML; use Compassplus\Sdk\Operation\Operation; use Compassplus\Sdk\Request\DataProvider; use Compassplus\Sdk\Service; use UnexpectedValueException; class OrderStatus extends DataProvider { public function __construct(Operation $operation) { $this->operation = $operation; } public function getRequestData() { return $this->getOrderStatusRequest(); } public function getOrderStatusRequest() { $service = new Service(); $xml = $service->write("TKKPG", [ "Request" => [ "Operation" => "GetOrderStatus", "Language" => $this->operation->merchant->getLanguage(), "Order" => [ "Merchant" => $this->operation->merchant->getId(), "OrderID" => $this->operation->order->getOrderId() ], "SessionID" => $this->operation->order->getSessionId() ] ]); if ($xml) { return $xml; } else { throw new UnexpectedValueException("XML is not generated"); } } } 