 namespace Compassplus\Sdk\Request\XML; use Compassplus\Sdk\Operation\Operation; use Compassplus\Sdk\Request\DataProvider; use Compassplus\Sdk\Service; use UnexpectedValueException; class Purchase extends DataProvider { protected $operation; public function __construct(Operation $operation) { $this->operation = $operation; } public function getRequestData() { $service = new Service(); $xml = $service->write("TKKPG", [ "Request" => [ "Operation" => "CreateOrder", "Language" => "", "Order" => [ "OrderType" => "Purchase", "Merchant" => $this->operation->merchant->getId(), "Amount" => $this->operation->order->getAmount(), "Currency" => $this->operation->order->getCurrency(), "Description" => $this->operation->order->getDescription(), "ApproveURL" => $this->operation->merchant->getApproveUrl(), "CancelURL" => $this->operation->merchant->getCancelUrl(), "DeclineURL" => $this->operation->merchant->getDeclineUrl(), "email" => $this->operation->customer->getEmail(), "phone" => $this->operation->customer->getPhone() ] ] ]); if ($xml) { return $xml; } else { throw new UnexpectedValueException("XML is not generated"); } } } 