<?php
 namespace Compassplus\Sdk\Request\XML; use Compassplus\Sdk\Operation\Operation; use Compassplus\Sdk\Request\DataProvider; use Compassplus\Sdk\Service; use UnexpectedValueException; class Reverse extends DataProvider { public function __construct(Operation $operation) { $this->operation = $operation; } public function getRequestData() { return $this->getRequestReverse(); } private function getRequestReverse() { $service = new Service(); $xml = $service->write("TKKPG", [ "Request" => [ "Operation" => "Reverse", "Language" => $this->operation->merchant->getLanguage(), "Order" => [ "Merchant" => $this->operation->merchant->getId(), "OrderID" => $this->operation->order->getOrderId() ], "Amount" => $this->operation->order->getAmount(), "Description" => $this->operation->order->getDescription(), "SessionID" => $this->operation->order->getSessionId() ] ]); if ($xml) { return $xml; } else { throw new UnexpectedValueException("XML is not generated"); } } } 