<?php
 namespace Compassplus\Sdk\Response; class Order extends Response { public function getPaymentUrl() { return $this->getURL() . '?SESSIONID=' . $this->getSessionID() . '&ORDERID=' . $this->getOrderId(); } public function getURL() { return $this->getString('URL'); } public function getSessionID() { return $this->getString('SessionID'); } public function getOrderId() { return $this->getInteger('OrderID'); } } 