<?php
 namespace Compassplus\Sdk\Response\XML; use SimpleXMLElement; use Compassplus\Sdk\Response\ResponseInterface; class Provider implements ResponseInterface { private $xml; public function __construct($responseBody) { $this->xml = new SimpleXMLElement($responseBody); } public function get($fieldName) { if (!empty($this->xml->xpath("//" . $fieldName)[0])) { return (string)$this->xml->xpath("//" . $fieldName)[0]; } return null; } public function getAttributeName($parentNode, $fieldName, $attributeValue) { $path = $parentNode . "/" . $fieldName . "[@name ='" . $attributeValue . "']" . "/@value"; return (string)$this->xml->xpath("//" . $path)[0]; } } 